css:
/*pc*/@media (min-width: 601px){
.top-bar {
  background-color: #000000;}
  .top-bar__page-links { color:#fff;}

  .top-bar__contacts-container{ color:#fff;}
      .header__logo {
        max-height: 80px;
        max-width: 800px;
    }
   #search-input {
    border: 1px solid #0d0d0d;
    border-radius: 30px;
}
  .header {
 box-shadow: -6px 15px 14px -9px rgba(0, 0, 0, 0.37); 
  }

  .header__nav {
    font-size: 14px;
    line-height: 50px;
    text-align: center;
    text-transform: none;
}

  .header__submenu {
    background: #f4f4f4;
    border-top: 1px solid #ffffff;
    margin-top: 0;}
  .header__nav a:hover {
    background-color: #000000fa;
    border-bottom-color: #000;
    color: #ffffff;
}
.page-title__head {
    color: #000000;font-size:20px;
}}
 /*mobile*/@media (max-width: 600px) {
   .page-title__head {
    color: #000000;font-size:18px;
}}

 /*responsivo*/

.h1, h1 {
    font-size: 20px;
}.product__sku {
    color: #a2a2a2;
    font-size: 12px;
}.breadcrumb-item.active {
    color: #000000;
}
.prices--big .prices__value {
    font-size: 24px;
}
.prices__discount span{color:#086623;}


