css:
/*pc*/@media (min-width: 601px){
main .top-bar {
  background-color: #000000;}
  .top-bar__page-links { color:#fff;}
  .top-bar__nav { background: #000;}


  .top-bar__contacts-container{ color:#fff;}
      .header__logo {
        max-height: 80px;
        max-width: 800px;
    }
   #search-input {
    border: 1px solid #0d0d0d;
    border-radius: 30px;
}
  .header {
 box-shadow: -6px 15px 14px -9px rgba(0, 0, 0, 0.37); 
  }

  .header__nav {
    font-size: 14px;
    line-height: 50px;
    text-align: center;
    text-transform: none;
}



  .header__submenu {
    background: #f4f4f4;
    border-top: 1px solid #ffffff;
    margin-top: 0;}
  .header__nav a:hover {
    background-color: #000000fa;
    border-bottom-color: #000;
    color: #ffffff;
}
.page-title__head {
    color: #000000;font-size:20px;
}}
 /*mobile*/@media (max-width: 600px) {
   .page-title__head {
    color: #000000;font-size:18px;
}}

 /*responsivo*/

.h1, h1 {
    font-size: 20px;
}.product__sku {
    color: #a2a2a2;
    font-size: 12px;
}.breadcrumb-item.active {
    color: #000000;
}
.prices--big .prices__value {
    font-size: 24px;
}
.prices__discount span{color:#086623;}
.share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}

.lead {
  font-size: 15px;}

.html-clearfix {
    padding: 15px 30px;
    border: 1px solid #bababa;
}

.py-lg-4 {
        padding-bottom: 0;

}
.footer__title[aria-expanded]{font-weight:700; font-size:24px;}

.footer__links i {
    color: #ffffff;
}.stamps img {
        max-height: 30px;
    }
.menu__item {
  border-bottom: var(--border-width) solid #ffffff;}

.menu__item a:hover {
        color: #ffffff;
        background: #000;
    }
.menu__list a:hover {background: #000;
text-decoration: none;}
.product__short-stock {
        display: none;
}
.product__favorite i {
    color: #000000;
    opacity: 1;
}
}
